import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import styled from 'styled-components';
import { useAuth } from '../contexts/AuthContext';
import { getMyOrders, updateOrderStatus, getShopOrders } from '../utils/foodItemService';
import { toast } from 'react-toastify';
import Loader from '../components/Loader';

const MyOrdersPage = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { currentUser } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    // Redirect to login if not authenticated
    if (!currentUser) {
      toast.info('Please log in to view your orders');
      navigate('/login');
      return;
    }

    const fetchOrders = async () => {
      try {
        setLoading(true);
        let data;

        // Different API calls based on user role
        if (currentUser.role === 'seller') {
          // For sellers, get orders for their shops
          // In a real app, we'd get the seller's shops first
          // For now, we'll just use a mock shop ID for the seller
          const shopId = 1; // Mock shop ID
          data = await getShopOrders(shopId);
        } else {
          // For customers, get their orders
          data = await getMyOrders(currentUser.id);
        }

        setOrders(data);
        setError(null);
      } catch (err) {
        setError('Failed to load orders. Please try again later.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, [currentUser, navigate]);

  const handleStatusUpdate = async (orderId, newStatus) => {
    try {
      await updateOrderStatus(orderId, newStatus, currentUser.id);

      // Update the local state to reflect the change
      setOrders(orders.map(order => 
        order.id === orderId ? { ...order, status: newStatus.toUpperCase() } : order
      ));
    } catch (err) {
      setError('Failed to update order status. Please try again.');
      console.error(err);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'PENDING':
        return 'warning';
      case 'PREPARING':
        return 'info';
      case 'READY':
        return 'success';
      case 'COMPLETED':
        return 'success';
      case 'CANCELLED':
        return 'error';
      default:
        return 'default';
    }
  };

  const formatDate = (dateString) => {
    const options = { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (loading) return <Loader />;

  return (
    <PageContainer>
      <PageTitle>
        {currentUser?.role === 'seller' ? 'Shop Orders' : 'My Orders'}
      </PageTitle>

      {error && (
        <ErrorMessage>
          {error}
        </ErrorMessage>
      )}

      {orders.length === 0 ? (
        <EmptyMessage>
          {currentUser?.role === 'seller' 
            ? 'No orders for your shops yet.' 
            : 'You haven\'t placed any orders yet.'}
        </EmptyMessage>
      ) : (
        <OrdersList>
          {orders.map((order) => (
            <OrderCard key={order.id}>
              <OrderHeader>
                <OrderNumber>
                  Order #{order.id}
                </OrderNumber>
                <StatusBadge status={order.status}>
                  {order.status}
                </StatusBadge>
              </OrderHeader>

              <OrderDetails>
                <OrderDate>
                  Placed on: {formatDate(order.created_at || new Date().toISOString())}
                </OrderDate>
                <ShopName>
                  Shop ID: {order.shop_id}
                  {currentUser.role === 'customer' && (
                    <ViewShopLink to={`/shops/${order.shop_id}`}>View Shop</ViewShopLink>
                  )}
                </ShopName>
                <OrderItems>
                  {order.items && order.items.map((item, index) => (
                    <OrderItem key={index}>
                      <ItemQuantity>{item.quantity}x</ItemQuantity>
                      <ItemName>{item.name}</ItemName>
                      <ItemPrice>${(item.price * item.quantity).toFixed(2)}</ItemPrice>
                    </OrderItem>
                  ))}
                </OrderItems>

                {order.notes && (
                  <OrderNotes>
                    <NoteLabel>Notes:</NoteLabel>
                    <NoteText>{order.notes}</NoteText>
                  </OrderNotes>
                )}

                <OrderTotal>
                  <TotalLabel>Total:</TotalLabel>
                  <TotalAmount>${order.total_price ? order.total_price.toFixed(2) : '0.00'}</TotalAmount>
                </OrderTotal>
              </OrderDetails>

              <OrderFooter>
                <StatusBadge color={getStatusColor(order.status)}>
                  {order.status}
                </StatusBadge>

                {currentUser.role === 'seller' && order.status === 'PENDING' && (
                  <ActionButtons>
                    <ActionButton 
                      color="info"
                      onClick={() => handleStatusUpdate(order.id, 'PREPARING')}
                    >
                      Start Preparing
                    </ActionButton>
                    <ActionButton 
                      color="error"
                      onClick={() => handleStatusUpdate(order.id, 'CANCELLED')}
                    >
                      Cancel
                    </ActionButton>
                  </ActionButtons>
                )}

                {currentUser.role === 'seller' && order.status === 'PREPARING' && (
                  <ActionButton 
                    color="success"
                    onClick={() => handleStatusUpdate(order.id, 'READY')}
                  >
                    Mark as Ready
                  </ActionButton>
                )}

                {currentUser.role === 'customer' && order.status === 'READY' && (
                  <ActionButton 
                    color="success"
                    onClick={() => handleStatusUpdate(order.id, 'COMPLETED')}
                  >
                    Mark as Received
                  </ActionButton>
                )}
              </OrderFooter>
            </OrderCard>
          ))}
        </OrdersList>
      )}
    </PageContainer>
  );
};

// Styled Components
const PageContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
`;

const PageTitle = styled.h1`
  font-size: 2rem;
  margin-bottom: 1.5rem;
  color: #333;
`;

const ErrorMessage = styled.div`
  color: #f44336;
  margin-bottom: 1rem;
  padding: 0.75rem;
  background-color: #ffebee;
  border-radius: 4px;
`;

const EmptyMessage = styled.p`
  color: #666;
  font-size: 1rem;
`;

const OrdersList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
`;

const OrderCard = styled.div`
  background-color: white;
