import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { FiMenu, FiX, FiShoppingBag, FiHome, FiList, FiPlusCircle, FiShoppingCart, FiUsers, FiLogIn, FiLogOut, FiUserPlus, FiUser } from 'react-icons/fi';
import { useAuth } from '../contexts/AuthContext';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const location = useLocation();
  const navigate = useNavigate();
  const { currentUser, isAuthenticated, isSeller, isCustomer, logout } = useAuth();

  // Close mobile menu when changing routes
  useEffect(() => {
    setIsOpen(false);
  }, [location]);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <NavbarContainer>
      <NavInner>
        <Logo to="/">
          <FiShoppingBag />
          <span>WildEats</span>
        </Logo>

        <MenuToggle onClick={() => setIsOpen(!isOpen)}>
          {isOpen ? <FiX /> : <FiMenu />}
        </MenuToggle>

        <DesktopNav>
          <NavLink 
            to="/"
            isActive={location.pathname === '/'}
          >
            <FiHome />
            <span>Home</span>
          </NavLink>

          {isAuthenticated && (
            <>
              <NavLink 
                to="/my-orders"
                isActive={location.pathname === '/my-orders'}
              >
                <FiList />
                <span>My Orders</span>
              </NavLink>

              <NavLink 
                to="/shops"
                isActive={location.pathname.startsWith('/shops')}
              >
                <FiShoppingCart />
                <span>Shops</span>
              </NavLink>

              {isSeller && (
                <NavLink 
                  to="/my-shop"
                  isActive={location.pathname.startsWith('/my-shop')}
                >
                  <FiShoppingBag />
                  <span>My Shop</span>
                </NavLink>
              )}

              {/* Only show Users link to sellers for now */}
              {isSeller && (
                <NavLink 
                  to="/users"
                  isActive={location.pathname.startsWith('/users')}
                >
                  <FiUsers />
                  <span>Users</span>
                </NavLink>
              )}
            </>
          )}

          {!isAuthenticated ? (
            <>
              <NavLink 
                to="/login"
                isActive={location.pathname === '/login'}
              >
                <FiLogIn />
                <span>Login</span>
              </NavLink>
              <NavLink 
                to="/register"
                isActive={location.pathname === '/register'}
              >
                <FiUserPlus />
                <span>Register</span>
              </NavLink>
            </>
          ) : (
            <NavButton onClick={handleLogout}>
              <FiLogOut />
              <span>Logout</span>
            </NavButton>
          )}
        </DesktopNav>
      </NavInner>

      <AnimatePresence>
        {isOpen && (
          <MobileNav
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: 'auto' }}
            exit={{ opacity: 0, height: 0 }}
            transition={{ duration: 0.3 }}
          >
            <MobileNavLink 
              to="/"
              isActive={location.pathname === '/'}
            >
              <FiHome />
              <span>Home</span>
            </MobileNavLink>

            {isAuthenticated && (
              <>
                <MobileNavLink 
                  to="/my-orders"
                  isActive={location.pathname === '/my-orders'}
                >
                  <FiList />
                  <span>My Orders</span>
                </MobileNavLink>

                <MobileNavLink 
                  to="/shops"
                  isActive={location.pathname.startsWith('/shops')}
                >
                  <FiShoppingCart />
                  <span>Shops</span>
                </MobileNavLink>

                {isSeller && (
                  <MobileNavLink 
                    to="/my-shop"
                    isActive={location.pathname.startsWith('/my-shop')}
                  >
                    <FiShoppingBag />
                    <span>My Shop</span>
                  </MobileNavLink>
                )}

                {isSeller && (
                  <MobileNavLink 
                    to="/users"
                    isActive={location.pathname.startsWith('/users')}
                  >
                    <FiUsers />
                    <span>Users</span>
                  </MobileNavLink>
                )}
              </>
            )}

            {!isAuthenticated ? (
              <>
                <MobileNavLink 
                  to="/login"
                  isActive={location.pathname === '/login'}
                >
                  <FiLogIn />
                  <span>Login</span>
                </MobileNavLink>
                <MobileNavLink 
                  to="/register"
                  isActive={location.pathname === '/register'}
                >
                  <FiUserPlus />
                  <span>Register</span>
                </MobileNavLink>
              </>
            ) : (
              <MobileNavButton onClick={handleLogout}>
                <FiLogOut />
                <span>Logout</span>
              </MobileNavButton>
            )}
          </MobileNav>
        )}
      </AnimatePresence>
    </NavbarContainer>
  );
};

const NavbarContainer = styled.nav`
  background-color: white;
